function urlencode(str,charset){
	  return Array.from(str.split('&')).map(v=>{
	  	  vindex=v.indexOf('=')
	     param1=v.substring(0,vindex)
	     param2=v.substring(vindex+1,v.length)
	     param2=java.encodeURI(param2,charset)
	     return String(`${param1}=${param2}`)
	  }).join('&')
}
function randomStr(){
	  resultstr=String("")
	  times=(Math.random()*6+15).toFixed(0)
	  function randomchar(){
	  	  chars='1234567890bcdefghijklmnopqrstuvwxyz'
      random=Math.random()
      cindex=Math.floor(random*chars.length)
      return chars.charAt(cindex)
	  }
	  while(resultstr.length<times){
	  	  resultstr=String(`${resultstr}${randomchar()}`)
	  }
	  return resultstr
}
