function getImgList(){     
var p = /newpagelabel\S+\/(\d+)/;     
var p1 = /src=\"(\/\/img\d.wnacg.download\/data\/\S+[^0])(\d+).(jpg||png)/;
var mp = html.match(p1);
var jpg = html.match(p)[1];
var  jpf = mp[2];
var jpl= mp[2].length;
var piclist = new Array(jpg); 
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = "https:"+mp[1]+(new Array(jpl).join('0') +(i+Number(jpf))).slice(-jpl)+"."+mp[3];
}
return piclist; 
}

